var Toast  = {};
var showIcon = 'data:image/png;base64,';

var errorIcon = 'data:image/png;base64,';

var infoIcon = 'data:image/png;base64,';

var typeMap = {
  success: showIcon,
  error: errorIcon,
  info: infoIcon
};

Toast.show = function(msg, type, duration){
  duration=isNaN(duration)?2000:duration;
  type = type ? type: 'info';
  var m = document.createElement('div');
  var s = document.createElement('div');
  var p = document.createElement('p');
  var img = document.createElement('img');

  img.src = typeMap[type];

  p.innerHTML = msg;
  img.onload = function(){
    img.style.cssText = "width: 30%;";
  };
  m.style.cssText="width:100%;height:40px;top:40%;position:fixed;z-index: 999999;";
  s.style.cssText="width: 28%; font-size: 2.8em; min-width: 1em; opacity: 0.8; height: 1; color: rgb(255, 255, 255); line-height: 1.5em; text-align: center; border-radius: 0.3em; z-index: 999999; font-weight: bold; margin: 0px auto; background-color: #000;padding: 2rem 0;";
  s.appendChild(img);
  s.appendChild(p);
  m.appendChild(s);
  document.body.appendChild(m);
  setTimeout(function() {
    var d = 0.5;
    m.style.webkitTransition = '-webkit-transform ' + d + 's ease-in, opacity ' + d + 's ease-in';
    m.style.opacity = '0';
    setTimeout(function() { document.body.removeChild(m) }, d * 1000);
  }, duration);
};

module.exports = Toast;
